import { useSelector, useDispatch } from "react-redux";
import { useState } from "react";
import {orderPokemon} from "../../redux/actions"
import style from "./Filter.module.css"

const Filter = () => {

    const allPokemons = useSelector((state) => state.allPokemons);
    const dispatch = useDispatch();
    const [aux, setAux] = useState(false);

    const handlerOrderPokemon = (event) => {
        dispatch(orderPokemon(event.target.value));
        setAux(true);
    };

    // const handlerFilter = (event) => {
    //     dispatch(filterCards(event.target.value));
    // };

    return (
        <div>
            <div className={style.Selector}>
            <select name="Order by" onChange={(e) => handlerOrderPokemon(e)}>
      <option key={0} value="" disabled selected>
            Order by
          </option>
        <optgroup label="Alphabet">  
          <option key={2} value="asc">
            Ascendant
          </option>
          <option key={3} value="des">
            Descendant
          </option>
        </optgroup>
        <optgroup label="Rating">
          <option key={4} value="ratingAsc">
            Worse to better
          </option>
          <option key={5} value="ratingDes">
            Better to worse
          </option>
        </optgroup>
      </select>
            </div>
        </div>
    )
}

export default Filter;
