import { connect, useDispatch } from "react-redux";
import { useState } from 'react';
import { getPokemonByName } from "../../redux/actions";
import style from "./SearchBar.module.css"

const SearchBar = () => {
  const dispatch = useDispatch()
  const [ pokemonName, setPokemonName ] = useState("")

  const handleChange = (event) =>{
    event.preventDefault();
    setPokemonName(event.target.value);
  }

  const handleSubmit = (event) =>{
    event.preventDefault();
    if (pokemonName.length > 0) {
      dispatch(getPokemonByName(pokemonName.toLocaleLowerCase()));
      console.log(pokemonName)
      setPokemonName('');
      console.log(setPokemonName)
    }
  }

  return (
    <div className={style.SearchBar}>
      <form onSubmit={handleSubmit}>
        <div className={style.DivInput}>
          <input
            className={style.SearchInput}
            type="search"
            placeholder="Search a Pokemon..."
            value={pokemonName}
            onChange={handleChange}
          />
        </div>
        <div className={style.DivButton}>
          <button type="submit" className={style.SearchBarButton}>Search</button>
        </div>
      </form> 
    </div>
  )
}

const mapStateToProps = (state) => {    //Esto es para hacer el componente global xq sino se actualiza pero no cambia de estado. recibe el estad global como argumento y recibimos lo q queremos pasar al componente
  return {
      pokemons: state.pokemons,
      
  }
}

export default connect(mapStateToProps, null)(SearchBar);
