import { useDispatch } from "react-redux";
import {orderPokemon} from "../../redux/actions"
import style from "./Order.module.css"

const Order = ({ setCurrentPage }) => {

    const dispatch = useDispatch();

    const orderPokemonName = (event) => {
        dispatch(orderPokemon(event.target.value));
        setCurrentPage(1)
    };

    return (
        <div className={style.OrderContainer}>
            <p className={style.LabelOrder}>Sort by</p>
            <select name="OrderPokemonName" onChange={orderPokemonName} className={style.SelectorOrder}>
                <option key={0} value="" >Pokemon #</option>  
                <option key={1} value="all" > ALL</option>  
                <optgroup key={2} label="Alphabetical" font-weight="bold" disabled selected />  
                <option key={3} value="asc">A - Z</option>
                <option key={4} value="des">Z - A</option>
                <optgroup key={5} label="Attack" font-weight="bold" disabled selected />  
                <option key={6} value="str">STRONGEST</option>
                <option key={7} value="wea">WEAKEST</option>
            </select>
        </div>
    )
}

export default Order;
