import { useDispatch, useSelector } from "react-redux";
import {filterPokemon} from "../../redux/actions";
import style from "./Filter.module.css"

const Filter = ({ setCurrentPage }) => {

    const dispatch = useDispatch();
    const allTypes = useSelector(state=>state.allTypes)

    const handlerFilterBySource = (event) => {
        dispatch(filterPokemon(event.target.value));
        setCurrentPage(1)
    };

    //const handlerFilterByType = (event)

    return(
        <div className={style.FilterContainer}>
            <p className={style.LabelFilter}>Filter by</p>
            <select onChange={handlerFilterBySource} className={style.SelectorFilter}>
                <option key={0} value="all">ALL </option>  
                <optgroup key={1} label="Source" font-weight="bold" disabled selected />  
                <option key={2} value="original">Original</option>
                <option key={3} value="created">Created</option>
            </select>
            <select onChange={(event)=> handlerFilterBySource(event)} className={style.SelectorFilter}>
                <optgroup key={4} label="Type" font-weight="bold" disabled selected />
                <option key={5 + "event"} value="all">All Types</option>  
                {allTypes.map(n=>{
                    return(
                        <option value={n.name} key={n.id}>{n.name}</option>
                    )
                })}                
            </select>
        </div>
    )
}

export default Filter;
