import { useDispatch } from "react-redux";
import { useState } from 'react';
import { getPokemonByName, getAllPokemons } from "../../redux/actions";
import style from "./SearchBar.module.css"
import Loading from "../Loading/Loading";

const SearchBar = ({ setCurrentPage }) => {
  const dispatch = useDispatch()
  const [ pokemonName, setPokemonName ] = useState("")
  const [error, setError] = useState(null)

  const handleChange = (event) =>{
    event.preventDefault();
    setPokemonName(event.target.value);
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (pokemonName.length > 0) {
      try {
        const response = await dispatch(getPokemonByName(pokemonName.toLowerCase()));
        if (response.status === 404) {
          setError(response.message);
          setPokemons([]); // Limpiar los datos de pokemons en caso de error
        } else {
          const pokemons = response.data;
          setPokemons(pokemons);
          setError(null); // Limpiar el mensaje de error en caso de éxito
        }
      } catch (error) {
        console.log(error.message);
        setError('Error: Hubo un problema al buscar los pokemons.'); // Manejo genérico de error
        setPokemons([]); // Limpiar los datos de pokemons en caso de error
      }
      setPokemonName('');
    }
  };

  const handleReset = (event) => {
    event.preventDefault()
    dispatch(getAllPokemons())
  }

  return (
    <div className={style.SearchBar}>
      {!handleChange ? (
        <Loading></Loading>
      ):(
      <>
        <form onSubmit={handleSubmit}>
          <div className={style.DivInput}>
            <input
              className={style.SearchInput}
              type="search"
              placeholder="Search a Pokemon..."
              value={pokemonName}
              onChange={handleChange}
            />
          </div>
          <div className={style.DivButton}>
            <button type="submit" className={style.SearchBarButton}>Search</button>
            <button className={style.ResetButton} onClick={handleReset}>Reset</button>
          </div>
          {error && <p>{error}</p>}
        </form> 
      </>
      )}
    </div>
  )
}

export default SearchBar;
