import { useDispatch, useSelector } from "react-redux";
import { useState, useEffect } from 'react';
import { getPokemonByName, getAllPokemons } from "../../redux/actions";
import style from "./SearchBar.module.css"
import Loading from "../Loading/Loading";

const SearchBar = ({ setCurrentPage }) => {
  const dispatch = useDispatch()
  const [ pokemonName, setPokemonName ] = useState("")
  const pokemonNameState = useSelector(state=>state.pokemonName);
  const [loading, setLoading] = useState(true);

  const handleChange = (event) =>{
    event.preventDefault();
    setPokemonName(event.target.value);
  }

  const handleSubmit = (event) =>{
    event.preventDefault();
    useEffect ((name)=>{
      dispatch(getPokemonByName(name))
      .then(() => setLoading(false))
      .catch(() => setLoading(false))
  },[dispatch,name])
    if (pokemonName.length > 0) {
      dispatch(getPokemonByName(pokemonName.toLocaleLowerCase()));
      setPokemonName('');
      setCurrentPage(1)
    }
  }

  const handleReset = (event) => {
    event.preventDefault()
    dispatch(getAllPokemons())
  }

  return (
    <div className={style.SearchBar}>
      {loading?(
        <Loading></Loading>
      ): !pokemonNameState.id ? (
        <div>
          <h1 className={style.PokemonNotFounded}>That Pokemon doesn't exist</h1>
        </div>
      ) : (
      <>
        <form onSubmit={handleSubmit}>
          <div className={style.DivInput}>
            <input
              className={style.SearchInput}
              type="search"
              placeholder="Search a Pokemon..."
              value={pokemonName}
              onChange={handleChange}
            />
          </div>
          <div className={style.DivButton}>
            <button type="submit" className={style.SearchBarButton}>Search</button>
            <button className={style.ResetButton} onClick={handleReset}>Reset</button>
          </div>
        </form> 
      </>
      )}
    </div>
  )
}

export default SearchBar;
