import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router";
import {getPokemonDetail} from "../../redux/actions";
import style from "./Detail.module.css";

const Detail =() =>{

    const {id} = useParams();
    const dispatch = useDispatch();
    const detail = useSelector(state=>state.detail);

    useEffect (()=>{
        dispatch(getPokemonDetail(id))
    },[dispatch,id])

    return (
        <div className={style.DetailContainer}>
            <div>
                {detail.name ? (
                    <div className={style.loading}> 
                        <img className={style.LoadingImage} src="https://www.pngmart.com/files/22/Mr.-Rime-Pokemon-PNG.gif" alt="gif"></img>
                    </div>
                ):(
                    <div>
                        <div> 
                            <h2> {detail.id} </h2>
                            <h2> {detail.Name} </h2>
                            <img src={detail.image} alt="imagen"/>
                            <p> attack:{detail.Attack}</p>
                            <p> defense:{detail.Defense}</p>
                            <p> hp:{detail.Hp}</p>
                            <p> speed:{detail.Speed}</p>
                            <p> height:{detail.Height}</p>
                            <p> weight:{detail.Weight}</p>
                            {/* <p> types:{detail.Types.map((v) => v.name).join(' / ')}</p> */}
                        </div>
                    </div>
                    
                )}
            </div>
        </div>
    )
}

export default Detail;
