import React from "react";
import "./Paginacion.module.css";
import {useSelector} from "react-redux"

const Paginacion = () => {

    const listadoArticulosDOM = "#listado-articulos";
    const botonAtrasDOM = "#atras";
    const informacionPaginaDOM = "#informacion-pagina";
    const botonSiguienteDOM = "#siguiente";
    const plantillaArticulo = "#plantilla-articulo"
    const elementosPorPagina = 12;
    let paginaActual = 1;
    
    const allPokemons = useSelector(state=>state.allPokemons);

    function avanzarPagina() {
        paginaActual = paginaActual + 1;
        renderizar();
    }
    function retrocederPagina() {
        paginaActual = paginaActual - 1;
        renderizar();
    }
    function obtenerRebanadaDeBaseDeDatos(pagina = 1) {
        const corteDeInicio = (paginaActual - 1) * elementosPorPagina;
        const corteDeFinal = corteDeInicio + elementosPorPagina;
        return allPokemons.slice(corteDeInicio, corteDeFinal);
    }
    function obtenerPaginasTotales() {
        return Math.ceil(allPokemons.length / elementosPorPagina);
    }
    function gestionarBotones() { // Para comprobar que no se pueda retroceder
        if (paginaActual === 1) {
            botonAtrasDOM.setAttribute("disabled", true);
        } else {
            botonAtrasDOM.removeAttribute("disabled");
        }
        if (paginaActual === obtenerPaginasTotales()) { // Para comprobar que no se pueda avanzar
            botonSiguienteDOM.setAttribute("disabled", true);
        } else {
            botonSiguienteDOM.removeAttribute("disabled");
        }
    }
    function renderizar() { // Limpiamos los art칤culos anteriores del DOM
        listadoArticulosDOM.innerHTML = ""; // Obtenemos los art칤culos paginados
        const rebanadaDatos = obtenerRebanadaDeBaseDeDatos(paginaActual);
        gestionarBotones();
        informacionPaginaDOM.textContent = `${paginaActual}/${obtenerPaginasTotales()}`;    // Crear un art칤culo para cada elemento que se encuentre en la p치gina actual
        rebanadaDatos.forEach(function (datosArticulo) {
            const miArticulo = plantillaArticulo.cloneNode(true);
            const miTitulo = miArticulo.querySelector("#titulo");
            miTitulo.textContent = datosArticulo.title;
            const miCuerpo = miArticulo.querySelector("#cuerpo");
            miCuerpo.textContent = datosArticulo.body;
            listadoArticulosDOM.appendChild(miArticulo);
        });
    }
    botonAtrasDOM.addEventListener("click", retrocederPagina);
    botonSiguienteDOM.addEventListener("click", avanzarPagina);

    renderizar(); // Mostramos la primera p치gina nada m치s que carge la p치gina
    
   return(
        <div>
            <section>
                <div id="listado-articulos"></div>
                <div>
                    <button id="atras">游녣</button>
                    <span id="informacion-pagina"></span>
                    <button id="siguiente">游녤</button>
                </div>
            </section>
            <template id="plantilla-articulo">
                <article>
                <h2 id="titulo">titulo</h2>
                <p id="cuerpo">cuerpo</p>
                </article>
            </template>
        </div>
    )
};

export default Paginacion;
