import { useSelector, useDispatch } from "react-redux";
import { useState } from "react";
import {orderPokemon} from "../../redux/actions"
import style from "./Order.module.css"

const Order = () => {

    const allPokemons = useSelector((state) => state.allPokemons);
    const dispatch = useDispatch();
    const [aux, setAux] = useState(false);

    const handlerOrderPokemonName = (event) => {
        dispatch(orderPokemon(event.target.value));
        setAux(true);
    };

    // const handlerFilter = (event) => {
    //     dispatch(filterCards(event.target.value));
    // };

    return (
        <div className={style.OrderContainer}>
            <p className={style.Label}>Sort by</p>
            <select name="OrderPokemonName" onChange={handlerOrderPokemonName} className={style.SelectorOrder}>
                <option key={0} value="title" disabled selected> </option>  
                <optgroup key={1} label="Alphabetical" font-weight="bold" disabled selected />  
                <option key={2} value="asc">A - Z</option>
                <option key={3} value="des">Z - A</option>
            </select>
        </div>
    )
}

export default Order;
